    }

    .notification .title {
      font-weight: bold;
      font-size: 15px;
      line-height: 17px;
      color: #252525;
    }

    .notification .title-cover {
      left: 17.3px;
      top: 55px;
    }


    .notification .description {
      font-weight: 500;
      font-size: 12px;
      line-height: 15px;
      color: #414141;
    }

    .notification .description-cover {
