  function sendDemo(e) {
    if (!demoSending) {
      name = document.getElementById('demo-name').value;
      email = document.getElementById('demo-email').value;
      phone = document.getElementById('demo-phone').value;
      company = document.getElementById('demo-company').value;
      token = "H0OUVqZlDPLNpEk28UhPG0Ca4ynREyQJbd4W9yjv";
      demoSending = true;

      formData = new FormData();
      formData.append("name", name);
      formData.append("email", email);
      formData.append("phone", phone);
      formData.append("company", company);
      formData.append("_token", token);
      formData.append("type", "demo");


      $('.demoModal #modal-form').attr('class', 'd-flex flex-column position-relative animate__animated animate__fadeOut');
      $('.demoModal #modal-loading').attr('class', 'd-flex flex-column animate__animated animate__fadeIn position-absolute');

      fetch('https://larapush.com/api/newDemoRequest', {
        method: 'post',
        body: formData,
      }).then(res => res.json()).then(data => {
        if (data.success) {
          // opacity: 0; form
          $(e).css('opacity', '0');
          $('.demoModal #modal-loading').attr('class', 'd-flex flex-column position-absolute animate__animated animate__fadeOut');
          $('.demoModal #modal-success').attr('class', 'd-flex flex-column animate__animated animate__fadeIn position-absolute');
        }
      })
    }

    return false
  }

  function sendBF(e) {
    if (!bfSending) {
      name = document.getElementById('bf-name').value;
