  function hideSidebar() {
    document.querySelector('.sidebar').style.transform = 'translateX(-100%)';
    document.querySelector('.sidebar-backdrop').style.opacity = '0';
    setTimeout(() => {
      document.querySelector('.sidebar-backdrop').style.display = 'none';
