            <div class="date secondary-text">${prev} days ago</div>
            <div class="header">${d.title}</div>
            <div class="text">${d.details}</div>
            <div class="name secondary-text">${d.name}</div>
        </div>`

        prev += Math.floor(Math.random() * 5)
      });

      localStorage.setItem("reviews", reviews);

      $('.tp-widget-reviews').html(reviews);
    }

    addReview()


    var scrollAtATime = 1
    var total = $('.tp-widget-review').length - 1
    var currentTP = 0
    var TPWidth = 275

    if ($('.tp-widget-reviews-wrapper').width() < 275) {
      $('.tp-widget-review').width($('.tp-widget-reviews-wrapper').width() - 20)
      TPWidth = $('.tp-widget-reviews-wrapper').width() - 20
    } else {
      $('.tp-widget-review').width(275)
      scrollAtATime = Math.floor($('.tp-widget-reviews-wrapper').width() / 275);
    }

    function move(direction) {
      if (direction == 'left') {
        if (currentTP == 0) {
          return
        }

        if (currentTP - scrollAtATime < 0) {
          currentTP = 0
        } else {
          currentTP -= scrollAtATime
        }
      } else {
        if (total >= currentTP + scrollAtATime) {
          currentTP += scrollAtATime
        }
      }

      $('#tp-widget-reviews').css({
        'transform': 'translateX(-' + $('.tp-widget-review')[currentTP].offsetLeft + 'px)'
      });

      if (currentTP == 0) {
        $('#review-arrow-left').addClass('display-none')
      } else {
        $('#review-arrow-left').removeClass('display-none')
      }

      if (total >= currentTP + scrollAtATime) {
        $('#review-arrow-right').removeClass('display-none')
      } else {
        $('#review-arrow-right').addClass('display-none')
      }
    }

    $('#review-arrow-left').click(function() {
      move('left')
    })

    $('#review-arrow-right').click(function() {
      move('right')
    })
  });
</script>
