  <script defer src="https://cdn.jsdelivr.net/npm/keen-slider@5.5.1/keen-slider.js"></script>
  <script>
    $(document).ready(function() {

      var slider = new KeenSlider("#small-slider", {
        mode: "snap",
        spacing: 0,
        loop: 1,
        created: function(instance) {
          document
            .querySelector(".small-slider #arrow-left")
            .addEventListener("click", function() {
              instance.prev()
            })

          document
            .querySelector(".small-slider #arrow-right")
            .addEventListener("click", function() {
              instance.next()
            })

          var dots_wrapper = document.querySelector(".small-slider #dots")
          var slides = document.querySelectorAll(".small-slider .keen-slider__slide")
          slides.forEach(function(t, idx) {
            var dot = document.createElement("button")
            dot.classList.add("dot")
            dot.setAttribute("aria-label", "change slider")
            dots_wrapper.appendChild(dot)
            dot.addEventListener("click", function() {
              instance.moveToSlide(idx)
            })
          })
          updateClassesSmallSlider(instance)
        },
        slideChanged(instance) {
          updateClassesSmallSlider(instance)
        },
      })

      function updateClassesSmallSlider(instance) {
        var slide = instance.details().relativeSlide

        var dots = document.querySelectorAll(".small-slider #dots .dot")
        dots.forEach(function(dot, idx) {
          idx === slide ?
            dot.classList.add("dot--active") :
            dot.classList.remove("dot--active")
        })
      }

      if (window.location.href.indexOf('#demo') != -1) {
        $('#demoModal').modal('show');
      }
      if (window.location.href.indexOf('#black-friday') != -1) {
        // turn this off once black friday is over
        var bfOn = false;
        if (bfOn) {
          $('.bfModal').modal('show');
        } else {
          $('#demoModal').modal('show');
        }
      }

      // select all accordion buttons
      const accItems = document.querySelectorAll(".accordion__item");
      const accButtons = document.querySelectorAll(".accordion__btn");
      const accContents = document.querySelectorAll(".accordion__content");

      function toggleAcc() {
        const thisContent = this.nextElementSibling;
        const thisItem = this.parentNode;

        //
        accItems.forEach((content) => {
          if (content.querySelector(".accordion__content") != thisContent) {
            content.classList.remove("accordion__item--active");
            content.querySelector(".accordion__content").style.maxHeight = null;
          }
        });

        if (!thisContent.style.maxHeight) {
          thisContent.style.maxHeight = thisContent.scrollHeight + "px";
          thisItem.classList.add("accordion__item--active");
        } else {
          thisContent.style.maxHeight = null;
          thisItem.classList.remove("accordion__item--active");
        }
      }

      accButtons.forEach((acc) => acc.addEventListener("click", toggleAcc));

      $('.box-video').on('click', function() {
        $('#video-container').html('<iframe width="590" height="332" src="https://www.youtube.com/embed/0B59H1XqOm8?rel=0&showinfo=0&autoplay=1" frameborder="0" allowfullscreen="allowfullscreen"></iframe>')
        $(this).addClass('open');
      })
    });
  </script>

